window.onload=function(){var d=document.getElementsByClassName("accordion"),a,b;for(a=0;a<d.length;a++){var e=d[a].getElementsByTagName("h2"),f=e.length;for(b=0;b<f;b++){var c=e[b];c.setAttribute("class","toggle");c.nextElementSibling.setAttribute("class","closed");c.onclick=function(){"toggle"==this.getAttribute("class")?(this.setAttribute("class","toggle-active"),this.nextElementSibling.setAttribute("class","")):(this.setAttribute("class","toggle"),this.nextElementSibling.setAttribute("class","closed"))}}}};